func TestMergeSort(t *testing.T) {
	for _, testCase := range testCases {
		arr := testCase.arr
		res := testCase.res
		utils.MergeSort(arr)
		for i, v := range arr {
			if res[i] != v {
				t.Fatal("values at", i, "don't match. excepting:", res[i], "got:", v)
			}
		}
	}
}

func TestSort(t *testing.T) {
	for _, testCase := range testCases {
		arr := testCase.arr
		res := testCase.res
		slices.Sort(arr)
		for i, v := range arr {
			if res[i] != v {
				t.Fatal("values at", i, "don't match. excepting:", res[i], "got:", v)
			}
		}
	}
}
